mapper.insert:
insert into sys_dict (
    dict_code, dict_name, sort, status, create_time, del_flag
) values (
             #{dictCode}, #{dictName}, #{sort}, #{status}, now(), 0
         )

mapper.update:
UPDATE sys_dict
dict_code = #{dictCode}, dict_name = #{dictName}, sort = #{sort}, status = #{status}, update_time = NOW()
WHERE id = #{id}
