mapper.select:
select u.id, u.username, u.real_name, u.phone, u.dept_id, u.status, d.dept_name,u.create_time FROM sys_user u LEFT JOIN sys_dept d ON u.dept_id = d.id
and u.username like concat('%', #{username}, '%')
and u.real_name like concat('%', #{realName}, '%')
and u.phone like concat('%', #{phone}, '%')
and u.status = #{status}
and u.dept_id = #{deptId}

mapper.insert:
insert into sys_user(
    username, password, real_name, phone, dept_id, status
) values(
            #{username}, #{password}, #{realName}, #{phone}, #{deptId}, #{status}
        )
