DROP TABLE IF EXISTS `sme_policy`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `sme_policy` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `title` varchar(255) NOT NULL COMMENT '政策标题',
  `policy_type` varchar(50) NOT NULL COMMENT '政策类型（关联sys_dict_item.item_code，字典编码policy_type）',
  `publisher_id` bigint unsigned NOT NULL COMMENT '发布人ID，关联sys_user.id',
  `content` longtext COMMENT '政策内容（富文本存HTML，移除默认值避免同类型错误）',
  `publish_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `is_top` tinyint NOT NULL DEFAULT '0' COMMENT '是否置顶：0-否 / 1-是',
  `is_show` tinyint NOT NULL DEFAULT '1' COMMENT '是否显示：0-隐藏 / 1-显示',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `del_flag` tinyint NOT NULL DEFAULT '0' COMMENT '逻辑删除：0-未删 / 1-已删',
  PRIMARY KEY (`id`),
  KEY `idx_publisher_id` (`publisher_id`),
  KEY `idx_is_top` (`is_top`),
  KEY `idx_is_show` (`is_show`),
  KEY `idx_del_flag` (`del_flag`),
  KEY `idx_policy_type` (`policy_type`)
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='政策发布表';
